import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

const CARDIFF_CENTER = [51.4816, -3.1791];

const PharmacyMap = () => {
  const [pharmacies, setPharmacies] = useState([]);

  useEffect(() => {
    fetch("/src/data/cardiff_pharmacies.json")
      .then((res) => res.json())
      .then((data) => setPharmacies(data));
  }, []);

  return (
    <MapContainer center={CARDIFF_CENTER} zoom={13} style={{ height: "500px", width: "100%" }}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {pharmacies.map((pharmacy, idx) => (
        <Marker key={idx} position={[pharmacy.latitude, pharmacy.longitude]}>
          <Popup>
            <strong>{pharmacy.name}</strong><br />
            {pharmacy.address}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default PharmacyMap;
